            WriteLiteral(@"<div class="""">
    <div class=""container"">
        <br>
        <h5 class=""dh right-align"">ކައުންސިލް އިދާރާގެ ޒިމްމާތައް</h5>
        <hr>
        <div class=""row"">
            <div class=""col m10 s12 pull-m1 right"">

                <ul class=""collection"">
                    <li class="" dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ޤާނޫނުއަސާސީއާއި ޤާނޫނާއި އެއްގޮތްވާގޮތުގެ މަތީން ރަށް ހިންގައި، ރަށް ތަރައްޤީކޮށް، މި
                        ޤާނޫނުގައި
                        ބަޔާންކޮށްފައިވާ ގޮތުގެމަތީން ރަށްވެހިކަމުގެ ޙިދުމަތްތަށް ދިނުން
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ރައްޔިތުންގެ ބައިވެރިވުމާއިއެކު ރަށުގެ ތަރައްޤީގެ ޕްލޭން އެކުލަވާލައި، މިގޮތުން އެކުލަވާލެވޭ
                        ރަށުގެ
                        ތަރައްޤީގެ ޕްލޭން އަތޮޅު ކައުންސިލަށް ފޮނުވުން.
                    </li>
                    <li class=""dh collection-item right-ali");
            WriteLiteral(@"gn news-parag"" style=""direction: rtl;"">
                        ރަށު ކައުންސިލުން އެކުލަވާލައި އަތޮޅު ކައުންސިލަށް ފޮނުވާ ތަރައްޤީގެ ޕްލޭނަށް
                        ރިއާޔަތްކުރެވިގެން ސަރުކާރުން އެކަށައަޅާ މަޝްރޫއުތަކުގެ ތެރެއިން ރަށު ކައުންސިލުން
                        ތަންފީޒުކުރުމަށް ރަށު ކައުންސިލާ ސަރުކާރުން ޙަވާލުކުރާ ތަރައްޤީގެ މަޝްރޫޢުތައް މި ޤާނޫނުގައިވާ
                        ގޮތުގެމަތީން ތަންފީޒުކުރުން.
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ތަރައްޤީގެ އެކިއެކި މަޝްރޫޢުތައް ހިނގާނުހިނގާގޮތް ބެލުމުގައި ސަރުކާރުގެ މިނިސްޓްރީތަކަށާއި
                        އަތޮޅު ކައުންސިލަށް އެހީތެރިވެދީ، ތަރައްޤީގެ މަޝްރޫއުތައް ތަންފީޒުކުރުމުގައި އަތޮޅު
                        ކައުންސިލަށް ޖަވާބުދާރިވެ، މިނިސްޓްރީތަކުންނާއި، އަތޮޅު ކައުންސިލުން ކަނޑައަޅާ ގޮތަކަށް
                        ގަވާއިދުން ރިޕޯޓު ހުށަހެޅުން.
                    </li>
                    <li class=""dh collection-item right-align n");
            WriteLiteral(@"ews-parag"" style=""direction: rtl;"">
                        މި ޤާނޫނުގެ ދަށުން ރަށު ކައުންސިލަށް ލިބިދީފައިވާ ބާރާއި މަސްއޫލިއްޔަތު އަދާކުރުމަށްޓަކައި ރަށު
                        ފެންވަރުގައި އެކަށައަޅަންޖެހޭ ސިޔާސަތުތައް އެކަށައަޅައި އެއާގުޅޭ ގަވާއިދު ހަދައި ހިންގުން.
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        މި ޤާނޫނުގެ ދަށުން ރަށު ކައުންސިލަށް ލިބިދީފައިވާ ބާރާއި، މަސްއޫލިއްޔަތު އަދާކުރުމަށްޓަކައި
                        ރަށު ރައްޔިތުންގެ ނަމުގައި ނިންމަންޖެހޭ ކަންކަން ނިންމުން.
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ރަށު ރައްޔިތުންގެ މާލިއްޔަތާއި އެންމެހައި ވަސީލަތްތަށް ބެލެހެއްޓުން.
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ދިވެހިރާއްޖޭގެ ބިމާބެހޭ ޤާނޫނާއި އެހެނިހެން ޤާނޫނުތަކާ އެއްގޮތ");
            WriteLiteral(@"ްވާ ގޮތުގެމަތިން އިގްތިޞާދީ
                        ނުވަތަ އިޖްތިމާއީ ބޭނުންތަކަށް ކައުންސިލްގެ ނަމުގައި ބިންބިމާއި ފަރާއި ފަޅު ހޯދުމާއި
                        މިލްކުކުރުމާއި އަދި މިފަދަ ތަންތަން އެހެން ފަރާތްތަކަށް ކުއްޔަށްދޫކުރުމާއި އަދި މިފަދަ
                        ތަންތަނުގައި އިންވެސްޓްކުރުމާއި މިފަދަ އިންވެސްޓްމަންޓްތައް ކައުންސިލްގެ ނަމުގައި މިލްކުކުރުން.
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        އަތޮޅު ކައުންސިލްގެ ލަފަޔާއި އިރުޝާދުގެ މަތިން އަދި ދިވެހިރާއްޖޭގެ ބިމާބެހޭ ޤާނޫނާއި ރަށުގެ
                        ލޭންޑް ޔޫސް ޕްލޭނާ އެއްގޮތްވާ ގޮތުގެ މަތިން ސަރުކާރުގެ ބިމާބެހޭ ޤައުމީ އިދާރާއިން ރަށު
                        ކައުންސިލާ ޙަވާލުކޮށްފައިވާ ފަޅާއި ބިން އެކި ބޭނުމަށް ކުއްޔަށް ދޫކުރުމާއި ދިނުން.
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        އިގްތިޞާދީ ނުވަތަ އިޖްތިމާއީ ބޭނުންތަކަށް ކައުންސ");
            WriteLiteral(@"ިލްގެ ނަމުގައި އުފުލޭ އަދި ނުއުފުލޭ މުދާ
                        ހޯދުމާއި މިލްކުކުރުމާއި ކައުންސިލްގެ ބެލުމުގެ ދަށުގައިހުންނަ މިފަދަ ތަކެތި، މި ގާނޫނުގެ ދަށުން
                        ހެދޭ ގަވާއިދުގައިވާ ގޮތުގެމަތިން، އެހެންފަރާތް ފަރާތަށް ކުއްޔަށް ދިނުމާއި ދޫކުރުން.
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ތަރައްޤީގެ ޙަރަކާތްތައް ހިންގުމަށް ބޭނުންވާ ފައިސާ ހޯދުމަށް ޤާނޫނުގައި ކަނޑައަޅާފައިވާ ޙުދޫދުގެ
                        ތެރޭގައި މާލީ އިދާރާތަކުން ދަރަންޏަށް ފައިސާ ނެގުމާއި އެފަދަ ދަރަނިތަކުގެ ޙިމާޔަތެއްގެ ގޮތުގައި
                        ކައުންސިލްގެ އެކިއެކި މާލީ ޙައްޤުތަކާއި މަޞްލަޙަތުތަކާއި މިލްކިއްޔާތުތައް ދަރަނި
                        ދޫކުރާފަރާތްތަކަށް ހުށަހެޅުމާއި ތަފާތު އެކިއެކި ކަންކަމަށް ފަންޑު އުފެއްދުން. </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ކައުންސިލްގެ މެންބަރުން ބަދަލުވާ ޙާލަތްތަކުގައާއި ކައުނ");
            WriteLiteral(@"ްސިލްގެ ނަން ބަދަލުވާ ޙާލަތްތަކުގައި
                        ކުރީގެ ކައުންސިލުން ކޮށްފައިވާ ކަންކަމަށް ޒިންމާ އުފުލުން.
                    </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        މީހުންގެ އަމިއްލަ ބިންތަކާއި މުދާ ޢާންމު ބޭނުމަށްޓަކައި ކޮންމެހެން ނަގަންޖެހިއްޖެނަމަ ޤާނޫނާ
                        އެއްގޮތްވާ ގޮތުގެމަތިން ޢަދުލުވެރި ބަދަލު ދިނުމަށްފަހު އެފަދަ ތަންތަން ނެގުން. </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ކައުންސިލްގެ ބެލުމުގެ ދަށުން ޙިދުމަތް ދެވޭ ތަންތަންތަނާއި ކައުންސިލް އުޅަނދުފަހަރާއި ރަށުގެ
                        މަގުތަކާއި، ޕާކު ފަދަ ޢާންމު ތަންތަނަށް ނަން ދިނުމާއި ނަން ބަދަލުކުރުން. </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ރަށްރަށުގެ މަގާއި ޕާކުތަކާއި މައިދާން ފަދަ ތަންތަން ވަގުތީގޮތުން ނުވަތަ ދާއިމީ ގޮތުން
                        ހު");
            WriteLiteral(@"ޅުވުމާއި ބަންދުކުރުން. </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ރަށުގައި ހުންނަ ދައުލަތުގެ މުއައްސަސާތަކާއި ސަރުކާރުގެ އިދާރާތަކަށް ބޭނުންވާނޭ އެހީތެރިކަމާއި
                        އެއްބާރުލުން ދިނުން. </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        މި ޤާނޫނުގައި ބުނެފައިވާ ރިޕޯޓްތައް ފޮނުވަންޖެހޭ ފަރާތްތަކަށް ފޮނުވުން. </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ކައުންސިލަށް ނިސްބަތްވާ ސިއްކަ ސަރުކާރުގެ ކަމާބެހޭ އިދާރާގައި ރަޖިސްޓްރީ ކުރުމަށްފަހު
                        ބޭނުންކުރުން. </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ދިވެހިރާއްޖޭގެ ޤާނޫނާ އެއްގޮތްވާ ގޮތުގެމަތިން ރަށުގެ ފަރާއި، ފަޅުފަދަ ޤުދުރަތީ ވަސީލަތްތަކުގެ
                        ބޭނުން ހެޔޮގޮތުގައި ހިފުން. </li");
            WriteLiteral(@">
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ރަށުގައި އަމާން އަދި ޞުލްޙަވެރި ވެއްޓެއް ޤާއިމްކުރުމަށްޓަކައި ފުލުހުންނާ ގުޅިގެން ކުރެވެންހުރި
                        އެންމެހައި ކަންކަން ކުރުން. </li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ކައުންސިލްގެ އިޙްތިޞާޞްގެ ތެރޭގައި ހިމެނޭ ކަންކަމާ ގުޅިގެން ނުވަތަ ކައުންސިލްގެ ދާއިރާގައި
                        އެއްވެސް މީހަކު ޤާނޫނާ ޙިލާފު ޢަމަލެއް ކޮށްފިކަމަށް ތުހުމަތުކުރެވޭނަމަ އެކަމެއް ތަޙްޤީގު
                        ކުރުމަށް ފުލުހުންގެ އެހީތެރިކަމަށް އެދުން.</li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ރަށު ކައުންސިލްގެ އިދާރާ ހިންގުން.</li>
                    <li class=""dh collection-item right-align news-parag"" style=""direction: rtl;"">
                        ޤާނޫނަކުން ކަނޑައަޅާ މިނޫންވެސް ކަންކަން ކުރުން.
            ");
            WriteLiteral("        </li>\n                </ul>\n\n\n            </div>\n        </div>\n    </div>\n</div>");
            EndContext();
